import './search.css'
import Header from '../header/header';
import Menu from '../menu/menu';

import React, { useState, useEffect} from 'react';
const API_url = "http://127.0.0.1:8000/meal"

function Search(){
  const[meals, setMeals] = useState([]);
  const [searchMeal, setSearchMeal] = useState('');

  const getMealRequest = async() =>{
      const url = `#{API_url}`;

      const response = await fetch(url);
      const responseJson = await response.json();

      if (responseJson.Search){
          setMeals(responseJson.Search);
      }

  }
  useEffect(()=> {
      getMealRequest();
  }, [searchMeal]);

  return(
      <div className="container-fluid movie-app">
          <div className="row">
              <Header searchMovie={searchMeal} setSearchMeal={setSearchMeal}/>
          </div>
          {/* <div className="row">
              <MovieListHeading heading='Search Result'/>
          </div> */}
          {/* <div className="row">
              <MealList movies={meals}/>
          </div> */}
      </div>
  )
}

export default Search;

