import './cart.css'

import React, { useState, useEffect,amount } from "react";


const Cart = ({ cart, setCart, handleChange }) => {
  console.log(amount)
  const [price, setPrice] = useState(0);

    const handlePrice = ()=>{
        let ans = 0;
        cart.map((item)=>(
            ans += amount*item.price
        ))
        setPrice(ans);
    }

    const handleRemove = (id) =>{
        const arr = cart.filter((item)=>item.id !== id);
        setCart(arr);
        // handlePrice();
    }

    useEffect(()=>{
        handlePrice();
    })

  return(
      <>
      {cart.map((item)=>(
                <div className="cart_box" key={item.id}>
                    <div className="cart_img">
                        <img src={item.image} />
                        <p>{item.name}</p>
                    </div>
                    <div>
                        <button onClick={()=>handleChange(item, amount)}> + </button>
                        <button>{amount}</button>
                        <button onClick={()=>handleChange(item, amount)}> - </button>
                    </div>
                    <div>
                        <span>{item.price}</span>
                        <button onClick={()=>handleRemove(item.id)} >Remove</button>
                    </div>
                </div>
            ))}
      <div className="total">
        <span>Total Price of your Cart</span>
        <span>Ksh {price}</span>
      </div>
      </>
  )

};

export default Cart;
