     e.preventDefault();
     setSearchInput(e.target.value);
   };

   if (searchInput.length > 0) {
       meals.filter((meal) => {
       return meal.name.match(searchInput);
   });
   }

   return <div>

   <input
      type="search"
      placeholder="Search here"
      onChange={handleChange}
      value={searchInput} />



