import './cart.css'
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import { Avatar, Card, Grid } from 'antd';

import React, { useState, useEffect } from "react";


const Cart = ({ cart, setCart, handleChange }) => {
  const column =[{
    heading : 'image',value: 'image'
  },
  {heading : 'name', value:'name'},
  {heading : 'price',value:'price'}
]
  const [price, setPrice] = useState(0);
  let [amount, setAmount] = useState(1);

    const handlePrice = ()=>{
        let ans = 0;
        cart.map((item)=>(
            ans += amount*item.price
        ))
        setPrice(ans);
    }

    const handleRemove = (id) =>{
        const arr = cart.filter((item)=>item.id !== id);
        setCart(arr);
        // handlePrice();
    }

    useEffect(()=>{
        handlePrice();
    })

    function handleClick(item){
      setAmount(amount++)
    }

    function handleMinus(item){
      cart.forEach((data)=>{
        if (data.id === item.id)
        setAmount(amount--)
      });

    }

  return(
      <>
      <table>
      <thead>
        <tr>
          {column.map((item, index) => <TableHeadItem item={item} />)}
        </tr>
      </thead>
      <tbody>
        {cart.map((item,index) => <TableRow item={item} />)}
      </tbody>
    </table>


    {cart.map((item)=>(
            <Card
            key={item.key}
            style={{
              width: 300,
            }}
            cover={
              <img
                alt="example"
                src={product.image}
                style={{height:"300px"}}
              />
            }
            actions={[
              <SettingOutlined key="setting" />,
              <EditOutlined key="edit" />,
              <EllipsisOutlined key="ellipsis" />,
            ]}
          >
            <Meta
              avatar={<Avatar src={product.image} />}
              title={product.title}
              description={product.description}
            />
            <button onClick={()=>clickHandler(product.id)}>Add to Cart</button>
          </Card>

        ))}






      {cart.map((item)=>(
                <div className="cart_box" key={item.id}>
                    <div className="cart_img">
                        <img src={item.image} />
                        <p>{item.name}</p>
                    </div>
                    <div>
                        <button onClick={()=>handleClick(item)}> + </button>
                        <button>{amount}</button>
                        <button onClick={()=>handleMinus(item)}> - </button>
                    </div>
                    <div>
                        <span>{item.price}</span>
                        <button onClick={()=>handleRemove(item.id)} >Remove</button>
                    </div>
                </div>
            ))}
      <div className="total">
        <span>Total Price of your Cart</span>
        <span>Ksh {price}</span>
      </div>
      </>
  )

};



export default Cart;

const TableHeadItem = ({ item }) => <th>{item.heading}</th>
const TableRow = ({ item, column }) => (
  <tr>
    {column.map((columnItem, index) => {

      if(columnItem.value.includes('.')) {
        const itemSplit = columnItem.value.split('.') //['address', 'city']
        return <td>{item[itemSplit[0]][itemSplit[1]]}</td>
      }

      return <td>{item[`${columnItem.value}`]}</td>
    })}
  </tr>
)
