import './search.css'
import Header from '../header/header';
import Menu from '../menu/menu';
import axios from "axios"

import React, { useState, useEffect} from 'react';
const url = "http://127.0.0.1:8000/meal"

function Search(){
  const[meals, setMeals] = useState([]);
 // const [searchMeal, setSearchMeal] = useState('');

  const searchMeal = (e) => {
    if (e.key==="Enter")
    {
      axios.get(`#{url}`)
      .then (res=>setMeals(res.data.meals))
      .catch(err=>console.log(err))
      console.log("This is it")
    }
  }

  const getMealRequest = async() =>{
      // const url = API_url;

      const response = await fetch(url);
      const responseJson = await response.json();

      if (responseJson.Search){
          setMeals(responseJson.Search);
      }

  }
  useEffect(()=> {
      getMealRequest();
  }, []);

  return(
      <div className="container-fluid movie-app">
          <form class="d-flex" role="search">
      <input class="form-control me-2" type="search" placeholder="Lasagnia" aria-label="Search"/>
      //value={meals} onChange= {e => setMeals(e.target.value)}/>

      <button class="btn btn-outline-success" type="submit">Search</button>
    </form>
      </div>
  )
}

export default Search;

