function App() {

  const [show,setShow] = useState(true)
  const [cart, setCart] = useState([]);

  const handleClick = (item) => {
    console.log(item)
    if (cart.indexOf(item) !== -1) return;
    setCart([...cart, item]);
  };
  const handleChange = (item, d) => {
    const ind = cart.indexOf(item);
    const arr = cart;
    arr[ind].amount += d;

    if (arr[ind].amount === 0) arr[ind].amount = 1;
    setCart([...arr]);
  };
  console.log(cart)
